document.addEventListener("DOMContentLoaded",function(){const o=document.querySelectorAll(".toggle-password");o.length>0&&o.forEach(e=>{const t=e.closest(".relative").querySelector("input[type='password'], input[type='text']"),s=e.querySelector(".eye-icon"),n=e.querySelector(".eye-slash-icon");!t||!s||!n||e.addEventListener("click",()=>{t.type==="password"?(t.type="text",s.classList.add("hidden"),n.classList.remove("hidden")):(t.type="password",s.classList.remove("hidden"),n.classList.add("hidden"))})})});
